INSERT INTO `book` (`id`, `name`, `cover`, `descr`, `year`, `author`, `category_id`, `country`, `language`, `date`, `page`) VALUES (1, '秦汉史讲义', 'http://localhost:9090/files/download/秦汉史讲义.jpg', '本书以秦晖在清华大学讲授“秦汉史”课程为蓝本，增加了近年来不断发现的考古资料，几经整理修改而成。\r\n\r\n不同于最初的课堂录音，此次成书比录音记录多出近一倍内容。而且，不同于传统的断代史著述，本书略于政治史，而从纵（时间上的周秦、汉唐对比）横（空间上的秦汉与罗马帝国对比）两个维度来探讨秦汉帝国那套政治经济制度和观念的由来，以及它们在秦汉以后的影响，高屋建瓴，对于当下也具有极为重要的借鉴意义。', '2024', '秦晖', 1, '中国', '中文', '2024-11', '524');
INSERT INTO `book` (`id`, `name`, `cover`, `descr`, `year`, `author`, `category_id`, `country`, `language`, `date`, `page`) VALUES (2, '自我', 'http://localhost:9090/files/download/自我.jpg', '“我想要实现自我，但我并不知道我是谁。”\r\n\r\n“在当今世界，‘失去一切’的风险是永久性的。拥有‘独特’知识的高级专业人员，可能会随着新技术的出现而残酷地沦为无能的人。”', '2024', '[英国] 齐格蒙特·鲍曼 / [爱沙尼亚] 瑞恩·罗德', 6, '英国', '英文', '2024-8-18', '304');
INSERT INTO `book` (`id`, `name`, `cover`, `descr`, `year`, `author`, `category_id`, `country`, `language`, `date`, `page`) VALUES (3, '偶遇：在漫游中感知艺术', 'http://localhost:9090/files/download/偶遇.jpg', '★在“漫游、偶遇、感知”中，与真实的世界狭路相逢\r\n\r\n★12组案例，尝试一种思考和谈论艺术的方式\r\n\r\n★巫鸿与陆智昌倾力合作，呈现纸书的另一种阅读方式', '2024', '巫鸿', 3, '美国', '英文', '2024-9', '384');
INSERT INTO `book` (`id`, `name`, `cover`, `descr`, `year`, `author`, `category_id`, `country`, `language`, `date`, `page`) VALUES (4, '怪画谜案', 'http://localhost:9090/files/download/怪画谜案.jpg', '日本国民级推理小说！《怪屋谜案》后，网红作家雨穴惊喜新作！\r\n\r\n单本爆卖80万册，作品狂销300万册！\r\n\r\n神级伏笔，高能反转，中文简体版专属双重赠品！', '2024', '雨穴', 4, '日本', '日文', '2024-7', '312');
INSERT INTO `book` (`id`, `name`, `cover`, `descr`, `year`, `author`, `category_id`, `country`, `language`, `date`, `page`) VALUES (5, '早春晴朗', 'http://localhost:9090/files/download/早春晴朗.jpg', '从南京到北京，二十二岁的她完成了人生第一次独自迁徙的壮举。\r\n\r\n她心里装着那么多对栾念的爱，栾念却只想借着她对他的喜欢肆意行凶。\r\n\r\n一生情窦初开的时光，不会再有了。', '2022', '姑娘别哭', 5, '中国', '中文', '2022-10', '336');
INSERT INTO `book` (`id`, `name`, `cover`, `descr`, `year`, `author`, `category_id`, `country`, `language`, `date`, `page`) VALUES (6, '真需求', 'http://localhost:9090/files/download/真需求.jpg', '在这个人人都在谈创新的时代，99%的人其实都在做无用功。为什么？因为不懂什么是“真需求”。\r\n\r\n梁宁的这本书，将颠覆你对商业的诸多认知。\r\n\r\n这不是一本教你如何成功的书，而是一本告诉你如何本质性思考的书。它会让你明白，为什么你的产品总是卖不动，为什么你的创新总是不被认可，为什么你的团队总是缺乏动力，为什么你一直努力却总在原地。', '2024', '梁宁', 6, '中国', '中文', '2024-11', '472');
INSERT INTO `book` (`id`, `name`, `cover`, `descr`, `year`, `author`, `category_id`, `country`, `language`, `date`, `page`) VALUES (7, '食南之徒', 'http://localhost:9090/files/download/食南之徒.jpg', 'ZUI贪吃的大汉使者唐蒙，\r\n\r\n来到了ZUI会吃的南越之国。\r\n\r\n这里食材丰富，简直就是饕餮之徒的梦想之地。然而，在美食背后，却涌动着南北对峙、族群隔阂、权位争斗、国策兴废……种种波谲云诡，竟比岭南食材的风味更加复杂。', '2024', '马伯庸', 7, '中国', '中文', '2024-4-1', '269');
INSERT INTO `book` (`id`, `name`, `cover`, `descr`, `year`, `author`, `category_id`, `country`, `language`, `date`, `page`) VALUES (8, '教育何用：重估教育的价值', 'http://localhost:9090/files/download/教育何用.jpg', '美国著名传播学家、媒介理论家尼尔·波兹曼一生关注教育问题。从最初在小学执教，到在纽约大学设立媒介环境学专业，培养出一批著名的传播学者，作为教育家的波兹曼不断思考着教育的本质与方法，在其媒介-技术批评理论的基础上，提出了一种致力于培养人文主义价值观的教育理论。这些思考的结晶就是《教育何用》。', '2024', '尼尔·波兹曼', 8, '美国', '英文', '2024-10', '264');
INSERT INTO `book` (`id`, `name`, `cover`, `descr`, `year`, `author`, `category_id`, `country`, `language`, `date`, `page`) VALUES (9, '控糖革命', 'http://localhost:9090/files/download/控糖革命.jpg', '对于现代人来说，忙着减肥、忙着美肤、忙着健身，但同时失眠、焦虑、痤疮、糖尿病等亚健康问题又如影随形。法国“葡萄糖女神”、生物化学家杰西·安佐斯佩告诉你，这些亚健康问题往往都是由我们体内的血糖水平变化引起的，只是我们不自知而已。若置之不理，皮肤问题、情绪问题往往就会接踵而至，情况严重的话，更会引发2型糖尿病、多囊卵巢综合征等身体问题。', '2024', '杰西·安佐斯佩（Jessie Inchauspé）', 6, '法国', '法文', '2024-1-1', '232');
INSERT INTO `book` (`id`, `name`, `cover`, `descr`, `year`, `author`, `category_id`, `country`, `language`, `date`, `page`) VALUES (10, '绝叫', 'http://localhost:9090/files/download/绝叫.jpg', '罗翔老师推荐！“这本书深刻地揭示了日本的社会问题，日本40多年来社会变迁的各种缩影。”\r\n\r\n豆瓣「2020年度读书榜单」推理·悬疑类top1！\r\n\r\n怀才者的自怜是天鹅的挽歌， 平庸者的崩坏是野兽的绝叫。', '2020', '叶真中显', 4, '日本', '日文', '2020-7', '440');
